SET FOREIGN_KEY_CHECKS=0;

-- ----------------------------
-- Table structure for `article`
-- ----------------------------
CREATE TABLE `article` (
  `aid` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT '文章标题',
  `click` int(11) NOT NULL COMMENT '点击次数',
  `description` text NOT NULL COMMENT '文章描述',
  `content` text NOT NULL COMMENT '文章内容',
  `source` varchar(255) NOT NULL COMMENT '文章来源',
  `author` char(255) NOT NULL COMMENT '文章作者',
  `orderby` int(11) NOT NULL COMMENT '文章排序',
  `linkurl` varchar(255) NOT NULL COMMENT '文章链接',
  `keywords` char(255) NOT NULL COMMENT '文章关键词',
  `iscommend` char(255) NOT NULL COMMENT '文章是否推荐',
  `ishot` char(20) NOT NULL COMMENT '文章是否热门',
  `thumb` varchar(255) NOT NULL COMMENT '缩略图',
  `category_cid` smallint(6) NOT NULL COMMENT '和栏目表的关联字段',
  `isrecycle` tinyint(10) NOT NULL DEFAULT '0' COMMENT '是否为回收站内容',
  `created_at` datetime NOT NULL COMMENT '创建时间',
  `updated_at` datetime NOT NULL COMMENT '更新时间',
  `wechat_keywords` varchar(255) NOT NULL,
  PRIMARY KEY (`aid`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` VALUES ('1', '“人本”服务+“GEEK”精神 Ongineer IT首获资本青睐', '84', '如今云计算市场竞争异常激烈，最大的特点就是技术的创新性、多样性总能给用户带来惊喜，在这样的背景下，巨头站队、版图分割，云计算的下半场已然开始，更多以“服务”著称的计划相继涌现。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><strong>近日，一直致力于IT咨询、服务与培训的技术企业Ongineer IT发布了新的企业级云服务产品，分别是奥工智能多云监控平台OCSM(OngineerCloud Smart Monitor)和奥工智能共享知识库 OGSH(Ongineer GeniusHall)，同时完成Pre-A轮融资。</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">今年的云计算市场备受资本青睐，表现活跃。据记者了解，Ongineer IT的融资活动得到了多家投资机构的关注。业内人称“老顽童”的多牛资本创始人蒋海炳说：“云计算市场竞争已经进入下半场，巨头已经分割版图形成格局，这个市场的特点是技术多样化、行业专属化，在这样的大背景下，云服务变的尤为重要。<strong>我们看到阿里腾讯等各巨头都投资了专属云服务的公司，如驻云，安畅等，因此像Ongineer IT这样中立、跨技术平台、跨行业领域，能够标准化云服务的专业公司将会有巨大的发展空间和很高的市场价值。”</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><a target=\"_blank\" href=\"http://s2.51cto.com/wyfs02/M01/9C/74/wKioL1lwe7LyzNtNAAHK949XSEc448.jpg-wh_651x-s_513922757.jpg\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img title=\"会议现场\" border=\"0\" alt=\"会议现场\" src=\"http://s2.51cto.com/wyfs02/M01/9C/74/wKioL1lwe7LyzNtNAAHK949XSEc448.jpg-wh_651x-s_513922757.jpg\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"background-color: rgb(255, 255, 0);\"><strong>解决问题，我们需要“全栈+极客”的理念</strong></span></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">“在云计算领域的长期实践中，我们发现在基础硬件层、系统平台层以及硬件软件层之间存在着三个问题，分别是选择多样、落地复杂和运维错位。<strong>尽管如今的云计算技术水平已经相当成熟，但对于云的标准一直没有得到统一，所以在选择、落地以及运维等诸多方面，都需要专业团队进行辅助让云在企业中得到最有效的利用。Ongineer IT就是要来解决这一系列的问题，如何解决?需要的是“全栈+极客”的理念。”</strong>Ongineer IT创始人兼总经理欧阳柳卿对记者说。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><a target=\"_blank\" href=\"http://s2.51cto.com/wyfs02/M02/9C/74/wKiom1lwe_ah9MDjAAIgGS5OcQo599.jpg\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img title=\"欧阳柳卿\" border=\"0\" alt=\"欧阳柳卿\" src=\"http://s2.51cto.com/wyfs02/M02/9C/74/wKiom1lwe_ah9MDjAAIgGS5OcQo599.jpg\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">&nbsp;<strong>Ongineer IT创始人兼总经理 欧阳柳卿</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">要达成在云服务过程中需要的“全栈+极客”的理念，一方面完成服务需要既了解技术硬件又知晓系统平台，还要明白软件应用的全栈工程师，以及在每个方向上专注于各自领域的极客工程师。这样前端负责服务客户的时候，如果领域工程师懂得基础硬件、系统平台，还懂得上层应用，就可以与客户实现无缝衔接，再加上后端有专项工程师和厂商专项工程师完成对接工作，就基本可以做到万无一失，但仅仅做到这样依然不够，另一方面Ongineer IT表示在服务过程中要具备自己的“眼睛”以及“大脑”。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">什么是自己的“眼睛”与“大脑”?Ongineer IT产品经理朱有志表示，产品最能说明一个企业的真正实力。“眼睛”就是智能多云监管平台OCSM，通过监管平台搜集信息，帮助工程师来解决前端问题。如果将OCSM比作人类的一个中枢神经，工作过程就像是通过神经网络进行实时数据采集，并将数据提供给工程师，就达到了精准度和速度;此外，智能共享知识库OGSH就是把所有的项目经验和成果都放到其中，最后形成Ongineer IT全栈云服务体系，今后遇到问题可以通过数据库进行快速的分析，找到最合适的解释方式提供给工程师并进行快速解决。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><span style=\"background-color: rgb(255, 255, 0);\"><strong>OCSM+OGSH 全栈云服务产品重拳出击</strong></span></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><strong>Ongineer IT智能多云监控平台OCSM和智能共享知识库OGSH是Ongineer IT“全栈云服务”的关键产品。</strong>其中OCSM针对各种私有云，公有云，混合云进行细致化监控，统计性能消耗分布并通过可视化的方式集中展示实时性能数据，依附实时的海量数据提供变更和调度智能决策的能力;从用户全生命周期、费用计量、报表、服务器、增强管理界面等多个管理维度实现与基础设施资源的融合。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><a target=\"_blank\" href=\"http://s3.51cto.com/wyfs02/M01/9C/74/wKiom1lwfD6y_M4OAAIRm4ve7v8847.jpg\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img title=\"朱有志\" border=\"0\" alt=\"朱有志\" src=\"http://s3.51cto.com/wyfs02/M01/9C/74/wKiom1lwfD6y_M4OAAIRm4ve7v8847.jpg\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">&nbsp;<strong>Ongineer IT产品经理 朱有志</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">“OCSM自身采用的是微服务的架构，功能是结耦，通过融合各类工具形成一个详细的监控指标，可以使监控范围扩展到CPU、硬盘等，当然不仅仅监控这些方面，还可以监控容器;OCSM本身拥有丰富的功能集合，包含用户管理，裸机管理、存储监控，可以一站式监控管理，降低运维成本。”朱有志解释道。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">另一款产品OGSH则是基于变更、故障、异常分析、预测等各个云服务场景，以分布式架构为基础，利用日制分析系统构成大数据设施并提供秒级搜索。通过快速查询、数据归档功能、共享使用自动解析日志、字段值过滤、集成云服务WorkFlow等，实现敏捷标准化云服务流程，通过分析并找到对应的解决方案，让功能进行服务标准化的呈现。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><strong>不同于云平台供应商将自身定义为云基础设施提供者，Ongineer IT这类云服务企业更像是用户使用云的贴身管家，可以帮助选对云、用好云。面对纷繁复杂的云计算市场， “云就绪的最后一公里服务”就显得格外重要。</strong>朱有志总结道，Ongineer IT将产品的智能云化规划分为三个阶段：</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><strong>通过一站式提供服务能力将壁垒打通整合成为workflow方式;</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><strong>基于海量数据提供智能决策能力，超越现在仅仅针对运维进行的智能分析并提供解决方案，延伸到多个场景，例如基于故障、变更，修改等不同的云服务场景，进而达成整体的智能决策;</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\"><strong>实现架构自育，一个结点出现故障的时候如何快速生成另一个健康的结点，将故障结点祛除进行愈合。</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" background-color:=\"\">欧阳柳卿表示，未来将全力加速Ongineer IT服务战略的落地，以便为客户带来更好的体验与更大的价值。加强与上游厂商的战略合作，包括与超大规模公有云、私有云和数据中心基础设施供应商等合作，帮助用户选对云，加速产品技术研发。通过软件工具实现云服务风险管控和成本优化，帮助客户用好云;加强公司团队能力建设，持续提升Ongineer IT的全栈云服务能力的同时提高用户满意度。</p><p><br/></p>', '51CTO.com', '刘晶晶', '1', 'http://cms.yanglichao.com/a_1.html', '云计算市场，技术的创新性、多样性，巨头站队、版图分割，云计算的下半场已然开始，更多以“服务”著称的计划相继涌现。', '1', '1', 'attachment/2017/07/25/7241500962962.jpg', '1', '0', '2017-07-21 12:27:59', '2017-07-25 14:09:24', '“人本”服务');
INSERT INTO `article` VALUES ('2', '人工智能，不止于技术的革命--WOT2017全球创新技术峰会开幕', '29', '2017年7月21日，由51CTO主办的WOT2017全球创新技术峰会在北京富力万丽酒店召开，大会以“人工智能，不止于技术的革命”为主题。在为期两天的峰会中，30余位来自微软、阿里、百度、京东、Uber的AI先行者将带着他们的独到见解以及实战经验，分别从学术研究、企业实践等多个维度展开有关AI的思想碰撞，一场场精彩分享将以全面化、专业化的视角为1000+技术同仁带来解决问题的方法和提高效率的新思路。', '<p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">作为一门学科，人工智能(AI)于1956年问世，历经一个甲子，几度沉浮。随着新一轮产业变革席卷全球，人工智能成为新一轮产业变革的核心方向。如今，人工智能已经无处不在，无人驾驶、金融、医疗、教育等等，只要有海量数据，人工智能就能渗入。毫无疑问，人工智能已经成为科技公司最重要的征战领域。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s4.51cto.com/wyfs02/M01/9C/86/wKioL1lxnLXRbseLAADFZqHzQC4702.jpg-wh_651x-s_3609368415.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s4.51cto.com/wyfs02/M01/9C/86/wKioL1lxnLXRbseLAADFZqHzQC4702.jpg-wh_651x-s_3609368415.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">2017年7月21日，由51CTO主办的WOT2017全球创新技术峰会在北京富力万丽酒店召开，大会以“人工智能，不止于技术的革命”为主题。在为期两天的峰会中，30余位来自微软、阿里、百度、京东、Uber、国美大数据中心、苏宁云商、顺丰的AI先行者将带着他们的独到见解以及实战经验，分别从学术研究、企业实践等多个维度展开有关AI的思想碰撞，一场场精彩分享将以全面化、专业化的视角为1000+技术同仁带来解决问题的方法和提高效率的新思路。除了场内的精彩演讲之外，场外还有动手实验室和科技体验区，让AI爱好者们在学习之余还能过把实践瘾。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s5.51cto.com/wyfs02/M01/9C/86/wKiom1lxnd3QHtwnAAJRsxlsnbo161.jpg-wh_600x-s_1204390919.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s5.51cto.com/wyfs02/M01/9C/86/wKiom1lxnd3QHtwnAAJRsxlsnbo161.jpg-wh_600x-s_1204390919.jpg\" border=\"0\" alt=\"大会开幕\" title=\"大会开幕\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">大会开幕</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">本届峰会共设三个技术分会场：机器学习、人机交互、智+应用。话题涉及数据处理、模型评估和优化、算法选择、系统设计、应用场景、软硬件结合、行业应用、案例分析、智能制造等。在21日上午的巅峰论坛中，京东技术战略专家吕建伟、 摩拜首席数据科学家尹大飞、西安电子科技大学教授焦李成、云知声CTO梁家恩，分别带来了黑科技引领电商新时代，大数据与人工智能技术在摩拜的应用，人工智能时代后深度学习的挑战与思考，智能交互技术与物联网应用等主题分享。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s4.51cto.com/wyfs02/M02/9C/86/wKioL1lxnhbyNBniAAG8AOfQ6x4184.jpg-wh_600x-s_1793559109.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s4.51cto.com/wyfs02/M02/9C/86/wKioL1lxnhbyNBniAAG8AOfQ6x4184.jpg-wh_600x-s_1793559109.jpg\" border=\"0\" alt=\"科技体验区\" title=\"科技体验区\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">科技体验区</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><span style=\"font-size: large\"><strong>京东吕建伟：黑科技引领电商新时代</strong></span></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">零售业态的变迁与技术的发展息息相关，黑科技在零售中发挥着越来越大的作用。此次分享，京东技术战略专家吕建伟为大家阐释了新零售与旧零售的区别，以及物联网IoT、人工智能、VR/AR等前沿科技如何升级新零售，并分享实际的场景案例。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s2.51cto.com/wyfs02/M02/9C/86/wKiom1lxnj7CEr2EAACW_cCtcT0228.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s2.51cto.com/wyfs02/M02/9C/86/wKiom1lxnj7CEr2EAACW_cCtcT0228.jpg\" border=\"0\" alt=\"吕建伟\" title=\"吕建伟\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">京东技术战略专家吕建伟</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">吕建伟首先提出自己的观点：人工智能一个非常重要的目的是帮助电商提供交易的转换复购。京东是一个带有交易型的公司。互联网更多的是营销流量，但是从营销流量转向交易，还有天壤之别，因此，一般的互联网公司想做电商就很困难。只有交易完成，才是真正给客户带来最终价值，因为这是要买单的。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">人工智能如何在线下流量跟踪，线上产品跟踪，线上线下如何统一交易，画像如何统一整合，这是当今电商要关注的重心。京东为自己走线上线下一体综合，做了很多技术上的储备。从应用上来说，京东切入交易，从营销、交易到企业的后端，全部提供了关键性的技术支撑，不管是线上的点击流量还是线下的统计，或者线上线下一体化用户行为跟踪，都有很多营销的技术嵌在里面。在交易方面，京东有很成熟的线上电商运营，包括仓储物流营，甚至无人机、无人车这样的嵌入人工智能自动化的科技技术。在企业后台处理，大量的音视频处理也用到了人工智能。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">从应用中间件方面来看，京东有自己的智能硬件公司和人工智能实验室，推出了自己的机器学习平台。吕建伟认为在这些支撑之下必然少不了数据。人工智能的核心其实在于数据，而且数据的处理加工是人工智能最大的工作量。也就是说，人工智能本质上是劳动密集型，它的很多数据标注和规范，甚至处理，只有给它吃下规范性的大量海量数据它才能够精准。所以说，需要一个完整的大数据平台。当然，大数据要想存储，就需要统一的云计算平台。从产品到营销，再到交易，京东在智能方面层层深入。吕建伟最后表示，人工智能能够在哪些商业应用方面带来业务价值，如何真正用技术带动业务价值的提升，这是每一位高级技术研发人员以及技术研发管理者追求的一个最终目标。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><span style=\"font-size: large\"><strong>摩拜尹大飞：人工智能助力百万辆单车精细化运营</strong></span></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">共享单车在不到一年的时间里成为城市一道彩色风景线，成为市民短途出行的首选工具。摩拜首席数据科学家尹大飞在演讲中详细介绍了摩拜单车如何利用大数据和人工智能技术解决运营中出现的违停、潮汐现象，以及在优化调度与运营方面的探索。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s3.51cto.com/wyfs02/M00/9C/86/wKioL1lxnmPRwnpzAADIVm9QVbw110.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s3.51cto.com/wyfs02/M00/9C/86/wKioL1lxnmPRwnpzAADIVm9QVbw110.jpg\" border=\"0\" alt=\"尹大飞\" title=\"尹大飞\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">摩拜首席数据科学家尹大飞</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">摩拜单车在最近一年多的运营期间，积累了不少的算法和技术，尹大飞认为人工智能技术一方面是在此基础上探索，一方面是如何和业务结合推动业务往前走。</p><p style=\"padding: 0px 0px 15px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">摩拜通过问卷调查，获取用户的使用感受，从而调整车辆设计。而投放地点、投放数量、以及调度方面的问题都通过算法驱动。与此同时，摩拜也在和相关专家探讨在智慧城市建设中，能否结合摩拜运营大数据，通过和城市规划部门的合作来合理规划智能推荐停车点的选址，为智慧城市交通体系的构建贡献自己的力量。<span style=\"font-family: 宋体;font-size: 16px\"></span></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><span style=\"font-size: large\"><strong>西安电子科技大学焦李成：人工智能时代后深度学习的挑战与思考</strong></span></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">大数据+大模型+大平台，使得深度学习成为人工智能时代的核心驱动力，从智能驾驶、机器翻译，再到无人值守超市等应用，“智造”背后的深度学习技术及网络模型也在不断的改进与创新。随着研究的不断深入，后深度学习时代已经超越了目前机器学习模型的神经科学观点，学习多层次、多通道组合的这一设计原则更加具有吸引力。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s2.51cto.com/wyfs02/M01/9C/86/wKioL1lxnovg-WYLAAERtNeQzUE768.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s2.51cto.com/wyfs02/M01/9C/86/wKioL1lxnovg-WYLAAERtNeQzUE768.jpg\" border=\"0\" alt=\"焦李成\" title=\"焦李成\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">西安电子科技大学教授焦李成</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">西安电子科技大学教授焦李成指出，当今，基于数据的大家在做，但基于知识的远远不够，基于海量大数据是热了，但考虑小样本、关键样本还不够，基于通用的是够的，稀疏性是不够的，感知、推理和决策是摆在我们目前的难题，对于深度来讲，同样也是新问题。当然，围绕智能制造，围绕“互联网+”，围绕人工智能，要做的事很多。未来，能够和谐的进行感知、应用，人机共处，是非常好的一件事情，但是要做到就不太容易。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">深度强化学习，深度迁移学习，从功能来说，还要加上感知、学习、训练，一直到现在要加入反馈、知识，要把学习变成一个动力学，变成一个网络，还有很长的路要走。深度学习是人工智能的一个重要方向，在未来，它将会不断出现激动人心的理论进展和应用实践，深刻影响我们生活的方方面面。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><span style=\"font-size: large\"><strong>云知声梁家恩：智能交互技术与物联网应用</strong></span></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">智联网时代即将到来，智能交互技术将在物联网得到广泛应用，推动物联网交互和服务的智能化升级。梁家恩的演讲主要分享了智能交互的技术体系、发展趋势与挑战，以及其在物联网产业的应用策略和实践经验。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\"><a href=\"http://s3.51cto.com/wyfs02/M02/9C/86/wKiom1lxnq7RWq6EAAD3_WyETJA565.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\"><img src=\"http://s3.51cto.com/wyfs02/M02/9C/86/wKiom1lxnq7RWq6EAAD3_WyETJA565.jpg\" border=\"0\" alt=\"梁家恩\" title=\"梁家恩\" style=\"border: none\"/></a></p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: center;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">云知声CTO梁家恩</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">在云知声梁家恩看来，这是一个智联网的时代，是互联网和人工智能的结合。互联网不能独立成为一个产业，要和产业紧密结合才能发挥很大价值，这个时代我们称为智联网时代，重新定义IoT。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">人工智能是从知识、规则、模板、符号推理开始的，用机器处理人类的符号，将人类知识赋予机器去解决问题。梁家恩认为，未来整个产业应用有非常大规模的渗透，不管是车载、家居、穿戴、手持设备，都是我们普通用户能够感知到的。智能系统更加了解用户后，可以满足人类服务这些需求。不管是医疗、教育、金融、交通、安防领域，主要是政府主导的智慧城市建设，这些方面人工智能都会有很大的应用空间。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">产业升级涉及到用户、企业、政府，需求是非常大的。整个技术，无论是AI技术突破，还是大数据包括超级运算能力的普及，都需要有很大基础。所以我们对这轮人工智能产业化有非常强的信心，我们认为不会是颗粒无收的状态。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">在下午的巅峰论坛中，来自饿了么的技术副总裁张浩，顺丰科技人工智能技术总监刘志欣，uSens技术研发副总裁马赓宇，云启资本执行董事陈昱，三角兽联合创始人兼CTO亓超，将发表：AI派来的外卖送餐员，人工智能助力物流升级，人机交互技术的未来发展，如何吸引人工智能投资人向你瞄准，如何消除机器人对人类的误解等主题演讲。同时还有主题为智能种子如何加速蜕变的圆桌对话环节，从技术的角度来观察行业的发展。</p><p style=\"padding: 0px 0px 15px;margin-top: 0px;margin-bottom: 0px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;;font-size: 16px;text-align: justify;line-height: 30px;word-wrap: break-word;white-space: normal;widows: 1;background-color: rgb(255, 255, 255)\">第二天的三大技术分会场，来自流利说、京东商城、百度、国美、微软等企业的知名专家将带来机器学习、人机交互、智+应用等领域的主题演讲，内容精彩，不容错过! 请持续关注<a href=\"http://wot.51cto.com/act/2017/innovation/?51cto\" target=\"_blank\" style=\"color: rgb(255, 66, 0);transition: all 0.5s ease\">WOT2017全球创新技术峰会官网</a>。</p><p><br/></p>', '51CTO.com', '赵立京', '2', 'http://cms.yanglichao.com/a_2.html', '017年7月21日，由51CTO主办的WOT2017全球创新技术峰会在北京富力万丽酒店召开，大会以“人工智能，不止于技术的革命”为主题。', '1', '1', 'attachment/2017/07/25/56911500962920.png', '10', '0', '2017-07-21 15:47:55', '2017-07-25 14:08:42', 'WOT2017');
INSERT INTO `article` VALUES ('3', '大数据时代，云计算如何为仿真技术插上“翅膀”', '6', '尽管国内高端的工业软件发展好多年，却没有在市场和产业上完成多大的转化。曾在美国ANSYS公司工作8年的张群博士毅然决然选择了回国创业，创办了英特仿真，致力于智能装备创新设计CAE软件产品的研发，以及为装备制造业创新提供仿真咨询服务。他认为，中国的CAE产业已经进入了发展的黄金期，张群博士希望通过CAE技术市场化、商业化和企业化的形式，把国内的自主研发品牌软件完善起来。近日，由英特仿真和华为合作的“工业仿真云平台”在大连正式上线。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">2017年7月7日，“2017年度国产工业软件优秀解决方案展示对接会——大连专场”在大连世博广场召开。在会上，由英特仿真和华为公司合作建立的“工业仿真云平台”正式上线，这是中国第一个仿真云平台。“工业仿真云平台”已初步计划在辽宁、江苏、山东、天津、湖北五省市与华为共同推广，而后再向全国其他地区辐射。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s4.51cto.com/wyfs02/M01/9C/3D/wKiom1lty2ewSWmQAAML95W3stU772.png-wh_651x-s_2180159665.png\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s4.51cto.com/wyfs02/M01/9C/3D/wKiom1lty2ewSWmQAAML95W3stU772.png-wh_651x-s_2180159665.png\" alt=\"\" title=\"\" border=\"0\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">前《连线》杂志主编克里斯•安德森曾经说过：“数字革命已经进入了真实世界的核心部分，而且可能已经产生了最深远的影响。”毫无疑问，信息科技发展所带来的创新与变化可谓翻天覆地，而利用云计算及数据构建模型，将成为未来仿真技术发展的主导力量。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">英特仿真公司总裁张群博士认为，无论从国家信息安全角度还是深层次创新设计对CAE技术和软件的依赖，我国的高端装备行业的设计、分析不可能一直依赖外国的分析软件和技术。同时，国家已经意识到了“重硬轻软”的问题，科技部和工信部都加深了对超算应用领域的投入，而支持大规模计算的CAE软件就是超算应用的重要部分之一。因此，英特仿真要走一条自主创新的道路，只要做到本地化，才有可能把自己的产品尽快完成市场化、企业化，难度在于：你要如何去做?</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">2009年，在积累了国际最优秀的CAE软件研发、管理和市场运作经验，以及国际人脉和资源后，张群选择回国在大连创建了英特工程仿真技术(大连)有限公司，致力于成为一家国际型的工程仿真软件研发公司和高端仿真技术服务供应商。很显然，张群的目标不止于完善CAE市场自有品牌的高端软件，还希望在汽车工程类、航天航空类、重工业和机械行业用仿真技术提供软件、定制化开发、技术咨询、技术服务，建立完整的仿真生态服务体系。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>让进口软件成为历史</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">为填补国内空白，张群一直想打造具有中国自主知识产权的商业化工程仿真及优化软件，为用户提供全方位的技术解决方案和技术咨询服务。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">“自主CAE研发这条路注定是崎岖不平的，充满了艰难险阻，其中有我们自身的原因，也有很多外部环境因素的影响，创业是冒险的，但是在大连我心里更有底气。”张群说。在2013年，依托大连良好的产业背景和高校资源，作为主要发起单位，英特仿真与大连高新区联合筹建了我国第一个CAE技术产业园“大连仿真产业园”，一起打造自主品牌CAE仿真分析软件，通过灵活的接口、精确的算法和开放的技术，定制化的用户交互平台开发，更加贴合了国内企业用户的实际应用状况。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">以前，国内一些不错的汽车公司CAE普及率相当高，产品设计各个环节，从零部件到整车，从动力学到强度分析各个方面都很完善，但是基本都照搬国外的模式，没有针对国内的流程或者工艺来适应它，所以中间使用上有大量的问题存在。张群认为，“尽管国内CAE软件应用已经普及，但我国拥有自主知识产权的计算机辅助工程产品极少，大部分依赖进口软件，价格昂贵。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">2016年，英特仿真与华为企业云在大连签署云计算战略合作协议，双方就共同打造云平台和推动云计算产业发展等领域达成战略合作。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">此次战略合作协议的签署，也标志着双方在工业仿真和云计算合作掀起了新的篇章，将共同推动工业仿真和云计算产业的发展，真正实现工业仿真产业与云计算全面结合，希望通过自主平台，与合作伙伴一起共赢云时代的未来。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">“通过仿真云平台的计算仿真和模拟技术，能够对产品进行设计、改进、创新，以及模型的快速认证和方案的快速对比，不但解决了大量零散复杂的CAE数据的可控性差的问题，还在安全性、耐久性、一致性方面得到保障，很大程度脱离企业对进口软件的依赖”， 张群冷静分析道。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">如今，中国的CAE产业已经进入了发展的黄金期，高端设备制造已经完全离不开以CAE为基础的数字化技术。曾几何时，通过想法改变世界是异常的艰难。如今，人们完全可以通过数字化虚拟技术来实现自己的想法和创意。而此时，数据不仅仅是让人认识这个世界，也将会改造现实世界。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>利用大数据拟合精确度</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">环境的改变带来了内心的冲击，经过多年的努力最终如期而至。英特仿真推出了工程多物理场仿真和优化软件平台，以及系列的行业专用计算机辅助工程技术产品。在整个制造行业中，包括汽车、航天航空、核电、机械重工为主提供专业性的软件和服务。张群表示，对仿真技术来说，可视化平台和前期建构平台应该是具备国内领先的优势。这也是十几年的积累基础，以及项目和推广的成果上比较靠前。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">明显能够从张群的语气中感受到，最先踏上这条路，并用自己创造的工具，成为生产力的感觉真好!但是在这个变革时代，所有的风起云涌都很难离开大数据的助推，如何利用大数据来提升仿真能力和精确度，是英特仿真接下来要深思的问题。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">过去，CAE技术是力学性能的分析计算以及结构性能的优化设计等问题的一种近似数值分析方法。通过力学特性分析来获取结果，是传统仿真技术的基础。无论是在航天航空的INTESIM颤振分析技术，还是在复合材料分层及损伤模拟上，都需要在实验室进行大量的反复试验对比来发现问题和预测问题，整个研发测试时间动辄几年，甚至更长的时间。现在仿真分析的发展趋势是渐渐的贯穿产品设计的全生命周期，过长的研发周期必然影响产品的市场竞争力。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">但是，大数据的出现为仿真技术提供了新的可能。传统的仿真结果大多都比较简单和直接，而大数据能够预先的处理和深入分析，为解决规模性的仿真数据提供全新的思路。大数据在仿真技术中一般分为两类：第一类是来自用户在互联网上活动的泛数据，多数是多源异构性、交互性、突发性、社会性等;另外一种是来自科学实验数据、工程传感数据等，这些数据使得仿真对象有了前所未有的扩展。随着数据量增长和数据种类增加的过程中，数据的密集性、时效性、完备性成了整个仿真系统生存的关键，如今，只要通过相关数值，就能够迅速把结果精准的计算出来。这归功于将人们平时所思所想，在一个虚拟的世界里进行重现。如今从制造到智造的转变，都离不开仿真技术的发展。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">“仿真技术在大数据上有两个层面，一个是本身模型，一次性海量存储的模型数据;另外一个则是大量的模型，规律性的问题需要大量的分析来实现”，张群认为。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">不过仿真技术一般跟大数据还有些区别，在设计的时候需要做一个基于实际物理问题的分析，比如航空发动机，不同的温度分布或者不同的设计间隙对性能的影响，要形成这种大量的分析需要通过分布调整，需要尝试100个、1000个、甚至10000个等不同的参数组合，来形成100个、1000个、10000个对应性，这也是张群所说的区域对区域，然后通过这种大量关系拟合出真正的敏感度，或是对性能和响应的对应关系。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">如果说，数据就是这个时代的生产力，那么除了以上的分析方式，我们应该如何结合起来把它当做生产力呢?</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">张群表示，“现在我们需要云平台，利用平台去进行模型的处理和模型的分析。这样能够同时让大量的客户在这个平台上应用这些数据进行分析和制作通用模型。例如，航空领域需要大规模的计算，仿真需要极高的软硬件配置，航空企业搭建满足需要的软硬件平台需要花费大量的人力物力，还存在资源分配不均、资源利用不充分等问题。引入云服务可以转变为按机时租用或在云平台上委托咨询服务等模式。避免了软硬件购置后可能的闲置和不充分使用的情况，在大幅度降低用户成本的同时，也更易于被用户采纳。云平台能够大幅度的降低该领域的软硬件计算仿真投入成本，不需要航空领域客户花费大量的人力物力去搭建。”</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">以上这种方式是用云平台来处理大数据分析。另外，英特仿真还针对一些数据进行轻量化，这也是对大数据的处理转化，能够在工作站上来完成。所以，英特仿真在管理庞大的数据上，通过自有的管理平台和云平台，然后对数据的梳理、分类、划分形成一个信息的管理，这对于生产力提升是非常重要的一块。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>云计算为仿真技术插上“翅膀”</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">随着大数据与云平台的结合，仿真技术逐渐进入以计算、存储为主导的世界，利用云计算本身的计算能力和存储能力，以及资源灵活利用，将会达到无所不在的强大软件体系，这是云计算给仿真技术带来的伟大便利。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">庞大的仿真数据分析、快速运算、存储需要到云平台的支撑，也是一项复杂的系统工程。首先要在IaaS层(基础架构即服务)需要提供强大、稳定、经济的计算基础措施，包括主机、存储、网络及其他硬件在内的硬件设备，这些是实现云服务的最基础资源，也包括维护人力、能源消耗、物业租赁、软/硬件维保、设备折旧等长期成本支出。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">对于一个创业公司来说，购买硬件，以及对软件的配置，建立自己的计算中心，需要大量的成本负担。张群认为，“对硬件的采购永远比使用量高，更多时候很多资源都是闲置的，造成资源上面的浪费。通过云计算，可以在有限成本的前提下，提供强大的硬件体系，以及整个强大的软件体系解决大量的计算问题，这对于仿真软件需要大量的数据处理、计算、存储，云计算能够很大程度解决这些问题。”</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">好在这几年随着云计算的发展，无论是成本、稳定性还是强大的云服务支持，看似难以解决的瓶颈都有了非常好的转机。在工业领域上，华为提供了强大的计算基础措施，包括处理CPU、内容、存储、网络和其他基本的计算资源，支撑了仿真技术上的一些硬性需求，并且在收益上，初期投资成本大大降低了，节约的大量成本客户可以更好的应用到研发中。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">云计算的发展，除了在仿真领域的门槛降低、资源利用、成本等问题上，张群认为，对仿真技术的普及推广度也有很大的影响，在仿真教学、医疗上，基于云的服务系统，包括辅助教学系统，以及大量的物理教学实验，可以快速建立一个虚拟实验体系，让学生真正有效利用这些数据和技术来完善自己的知识和对仿真的认知。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>携手打造工业仿真云平台</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">建设完成后，工业仿真云平台能够每年为企业节省超过4000万元的仿真相关软硬件投入。而且全国每年CAE软硬件投入在30-50亿元，云平台建成后短期内即有望覆盖5%的国内软硬件市场，将切实促进大连成为全国CAE技术应用产业基地、工业创新强市、两化融合强市、云应用强市。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">那么，工业仿真云平台到底能够为客户带来哪些价值呢?</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">张群表示，“工业仿真云平台不但节约客户的成本，而且还能帮助以往不可能实现仿真的企业应用仿真技术。工业仿真云平台不光提供服务、软件，还能打造APP的专用产品，并且进行打包发布，同时进行线上交易，打造了一个互动、交易、共享(模型共享，流程共享)的平台，在整个广度、宽度、客户成本、客户可获得的资源，都是无穷大的。”</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">华为作为全球领先的信息与通信解决方案供应商，在云服务领域具有先进的技术水平、运营能力和品牌影响力。双方在工业仿真和云计算合作上掀起了新的篇章，通过整合云计算产业链上下游资源，推动工业仿真和云计算产业在全国快速发展，真正实现工业仿真产业与云计算全面结合。华为将充分发挥在云服务领域技术和资源优势，携手英特仿真一起共同开启全国工业仿真和云计算产业发展的新征程。</p><p><br/></p>', '51CTO.com', '林师授', '3', '', '高端，工业软件，CAE技术，CAE软件，工业仿真云平台', '1', '1', 'attachment/2017/07/25/29451500962981.jpg', '11', '0', '2017-07-21 16:38:43', '2017-07-25 14:09:48', 'CAE');
INSERT INTO `article` VALUES ('4', '保护云端数据安全的三种方式', '0', '对于消费者来说，云技术是一个巨大福音：它能够让消费者以很低的成本或零成本储存海量的信息——音乐、信息、照片等等。借助各种各样的服务，人们可以随意存储更多的数据，而不用购买额外的设备，例如硬盘或记忆棒。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">对于消费者来说，云技术是一个巨大福音：它能够让消费者以很低的成本或零成本储存海量的信息——音乐、信息、照片等等。借助各种各样的服务，人们可以随意存储更多的数据，而不用购买额外的设备，例如硬盘或记忆棒。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">从这一点来看，云技术像一个巨型免费存储柜，但它也存在特殊的安全隐患。与实物文件柜不同的是，存储在云端的数据可能存在被全球网络罪犯盗窃的风险。如果这些不法分子发动袭击，他们很有可能会获得巨量的信息。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><img src=\"attachment/2017/07/21/20781500627017.jpg\"/></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">不法分子入侵云服务最常用的方式是盗取消费者密码，然后进入其账户。他们实现这一做法的方式包括，入侵云服务公司的数据库，欺骗消费者提供其密码(通常使用伪装成云服务公司的电子邮件)或直接猜测用户的密码，因为很多消费者如今仍在使用那些并不难猜的密码，例如“123456”或“password”。与此同时，消费者可能会重复使用同一密码，而此举会导致一连串的账户遭到入侵。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">消费者维权集团Public Knowledge网络安全政策总监Megan Stifle说：“鉴于在线业务的不断增长，消费者可能会在多个网站使用相同的密码。此举会将消费者置于险境。如果某个网站的密码遭到泄漏，例如其邮箱账户，那么黑客会尝试使用同样的密码和简单的密码变体来入侵用户收件箱中最为重要的网站。”</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">幸运的是，云服务公司非常重视云端安全这一问题。很多公司正在为消费者提供简单易用的方式来保障其数据的安全性。目前有三种比较稳妥的方式。当有人试图从陌生计算机登录账户时，这些方式都会要求登录者提供额外的信息。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第一种额外的安全保障方法使用的是简单的短信，诸如谷歌和微软这类公司在很久之前便已开始使用这种方法，即向消费者的手机发送验证码，然后要求在输入密码的同时输入验证码。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">尽管这种方法有其优点，但仍能够被顽固的不法分子攻破，因为他们可以欺骗通信运营商，让运营商将短信发送至自己的手机。这也是为什么一些用户转而使用其他解决方案来提升其云端安全的原因。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第二种方法靠的是手机应用，使用这类方法的公司包括Duo和Authy。这些应用提供一种不断变化的序列号，来作为消费者密码的一种辅助手段。该应用的方法相对来说更简便，因为用户无需等待短信，而且不法分子也无法通过欺骗运营商来进行破解。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第三种确保云数据安全的方法无需使用手机，但需要消费者在电脑上插入一个小型秘钥装置。这类设备中最常用的莫过于“Yubikey”，由名为Yubico的公司制造，价格仅为19美元。它能够兼容像Facebook和Dropbox这类常用的服务商。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">这种秘钥的方式更加安全，因为它要求用户在陌生电脑上登录时出具秘钥实体，而黑客做到这一点的可能性几乎为零。秘钥这种方式也更加迅速，因为它无需输入发送至手机的验证码。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">Yubico解决方案业务副总裁 Jerrod Chong说：“我们可以这样来看。人们开车需要钥匙，打开房门需要钥匙，秘钥则是证实人们在线身份的实体钥匙。从实体YubiKey中获取机密信息的难度比从手机应用中获取此类信息的难度要大得多。”</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">以上三种方法都十分便利。对于大多数消费者来说，最重要的一步在于告知为他们提供服务的各大云计算公司(包括谷歌、微软等等)，他们希望实施这些方法。此举涉及进入安全设置，添加额外的防御措施。这些措施通常又称为“双重验证”。(通常，寻找这一设置最简单的办法就是在网络上搜索“Dropbox双重验证”这类关键词。)</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">总之，越来越多的消费者正在利用云端来储存其最宝贵的信息，包括其文件和照片。随着人们对云服务的日益倚重，使用额外的安全验证手段比以往任何时候都更为重要。</p><p><br/></p>', '51CTO.com', '佚名', '4', '', '云技术是一个巨大福音，随意存储更多的数据，而不用购买额外的设备，例如硬盘或记忆棒。', '1', '1', 'attachment/2017/07/25/36941500963020.jpg', '12', '0', '2017-07-21 16:50:54', '2017-07-25 14:10:23', '云安全');
INSERT INTO `article` VALUES ('5', '云计算体验与成本双赢背后：需平衡集约、分布部署', '0', '云计算本质是一种基于互联网访问的IT服务能力，它具备按需随选的特点，具有良好的可视化体验，同时还具有经济性。按需分配以及弹性伸缩是云最为重要的特征，这种特点意味着云计算不仅可以及时满足各种需求，而且可以动态满足需求的变化。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">云计算本质是一种基于互联网访问的IT服务能力，它具备按需随选的特点，具有良好的可视化体验，同时还具有经济性。按需分配以及弹性伸缩是云最为重要的特征，这种特点意味着云计算不仅可以及时满足各种需求，而且可以动态满足需求的变化。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s2.51cto.com/wyfs02/M02/9C/4B/wKiom1luvquwjL6_AAG6zkdmDRs423.jpg-wh_651x-s_225393386.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s2.51cto.com/wyfs02/M02/9C/4B/wKiom1luvquwjL6_AAG6zkdmDRs423.jpg-wh_651x-s_225393386.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>弹性伸缩特性使网络成本降低</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">笔者先解释一下什么是云计算的按需分配以及弹性伸缩。以虚拟机为例，如这个时刻网络需要5个虚拟机，下一时刻增加到10个虚拟机，再下一时刻仅需要3个虚拟机，传统的IT模式很难满足这种动态伸缩需求，这种伸缩称为Scale Out/In，适合于原生云应用。而另一种伸缩体现在虚拟机本身资源能力的扩大和缩小，例如这一刻需要4vCPU/8G的虚拟机，下一刻扩大到8vCPU/16G，再下一刻回到4vCPU/8G，还可能包括虚拟机硬盘的伸缩，在这种伸缩的实际操作中，资源扩大比较容易，资源缩小则比较困难，通常不建议操作，这种伸缩称为Scale Up/Down，比较适用于传统IT平台，另外这种弹性伸缩一般是自动化的，不需要或很少需要人为干涉，由监控系统监控资源负载情况，基于预先定义的策略自动去调度资源、扩展和回收资源(对于Scale Up/Down这种伸缩在自动化伸缩情况下极其容易出现问题)。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">弹性伸缩这种特性对于互联网/移动互联网类应用是至为重要的，而要实现弹性伸缩就要求资源池共享程度高。资源池具有较大的规模，能够实现较好的“错峰填谷”，否则可能造成资源浪费(为了实现扩展，资源冗余度过高)或弹性不足(冗余不足，难以实现按需扩展)。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">除了弹性伸缩，云计算还具有经济性和低门槛的特点，成本优势是云计算之所以大获成功的关键之一，成本的降低促使中小企业和个人使用IT的门槛也随之降低(按需弹性也降低了用户使用云的门槛)。而云计算之所以有成本上的巨大优势，并非简单地引入虚拟化技术就能实现的(仅仅实现了单物理机的简单共享能力)，而要涉及到全局资源调度能力、产品模型优化(成熟度高的云服务提供商会提供上百种产品规格，以满足各种用户需求，避免用户资源浪费，实际也是资金浪费)、规模化经济效应、服务器高度定制化、 软件定义技术、自动化部署与运维技术、DC机房选址以及制冷电力节能技术的引入等，但集约规模化是最主要的降低成本的手段，有效降低建设、部署、运维的成本。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">另外，云计算的应用使得用户体验更优，而复杂度降低。传统IT的部署、应用复杂度都较高，需要专业人员操作，同时准备周期也长，而云计算用户能够像使用互联网应用一样使用云服务，例如可以自助服务、服务迭代优化、用户界面友好等，可以将大量的配置和运维工作交给后台的工程师。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">云架构要在集约和分布间平衡</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">因此，云计算的基础设施需要尽量集约化和规模化，以保障最大化的共享、按需提供资源以及降低成本，但过于集约化和规模化可能无法保障最终用户的访问体验，所以需要在集约和分布两者间实现平衡，为了保证用户使用体验，同时还要保证规模化效应。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">公有云服务商通常在数据中心的布局上费尽周章，例如AWS(亚马逊云计算公司)典型的数据中心架构(Region+Available Zone)，既要保证有规模，还要离用户需求更近一些，同时更要保证HA，在这方面AWS为行业(公有云)树立了标杆，目前大部分公有云提供商都按照AWS的云数据中心架构进行全球布局。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">同时，随着运营商网络功能虚拟化(NFV)的推进，运营商未来将需要构建面向NFV的规模庞大的云基础设施(即NFVI)，虽然承载的绝大部分将是软件化的网络网元，但NFVI的部署同样面临着集约化与分布式部署的矛盾。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">集约化部署的好处是建设、部署门槛较低，运营、运维成本更低，能够集中实现冗余和灾备，网络架构较为简单，管理统一较为容易，但是比较适合于承载计算存储型的网络VNF(网络虚拟化网元)，例如vIMS以及网元转控分离后的控制功能等，不适合于实现接入型、流量型和高转发型的网元。而例如OLT、BRAS、EPC转发设备等(严格说OLT和BRAS如果实现控制分离，控制功能的NFV可以实现一定的集约)，涉及到用户网络体验、网络架构调整和部署成本等问题。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">分布式资源池体系挑战巨大</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">因此，运营商的NFVI，无法做到像IT、VAS资源池那样集约化，需要参考网元现有部署位置以及网元虚拟化特点进行分层部署，甚至实现转控分离后，转发功能还需要进一步下沉，以提升用户体验，包括满足未来VR、4K、物联网以及自动驾驶等对带宽和时延要求高的业务需求，例如在本地网的边缘和核心层面的数据中心上进行部署，这样必然导致运营商NFVI的资源池节点体系非常分散，分布式的资源池体系带来的挑战巨大。主要面对的挑战有以下4点。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第一，本地网目前并没有合适的可部署NFVI的机房，需要充分利用本地网CO机房进行DC化改造，这带来CO选址、改造的挑战(有些CO机房改造成本过高，而新建机房又没有资源条件)。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第二本地网NFV容量较小，资源池规模较小，这就带来了大量的分布式的资源池建设问题，资源池的分裂(以及网络条件限制)难以实现共享和调度，建设部署成本也较高(需要分别进行规划、设计、集成部署等)，同时每个本地网的DC都需要有容灾的部署，增加了部署成本，另外在资源的维护上压力也较大，人工成本较高。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第三，各地部署节奏、需求和CO资源差异较大，这可能带来的问题是未来NFVI资源池五花八门，很难统一(例如软硬件厂商类型过多，架构封闭性，自成体系，形成一个个烟囱)，如何实现资源池统一部署架构，包括与已有资源池的管理架构融合，将是一个巨大的挑战。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">第四，管理部署和集成难度很大，未来的分散的每个资源池如何实现统一管理、调度和编排，管理层次和架构如何设计(NFVO、VIM、VNFM等)，与现有的OSS、网管如何配合和接口，都带来更多的复杂度。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">总而言之，未来的NFV资源池部署既要考虑电信网络设备特点和网络架构带来的分布式部署必然性，又要充分利用云资源池集约化部署优点，充分做好业务和DC规划，通过包括转控分离技术、业务流量分离技术(例如将VOIP、ITMS等业务从MSE/BRAS中分流)等技术实现一定的集约，同时也需要从资源池的部署架构(包括备份、容灾)和管理架构上进行统一规范，避免因为分布式部署带来的架构分裂和管理分散，而形成众多的烟囱式、小规模、封闭式NFVI资源池，同时为将来条件成熟时统一部署NFVI DCI网络，实现统一调度和业务编排创造基础条件。</p><p><br/></p>', '51CTO.com', '佚名', '5', '', 'IT服务能力，可视化体验，需平衡集约、分布部署', '1', '1', 'attachment/2017/07/25/53281500963049.jpg', '13', '0', '2017-07-21 16:54:51', '2017-07-25 14:10:52', '云体验');
INSERT INTO `article` VALUES ('6', '更适合物联网的边缘计算，可能只是投机者的一块鸡肋', '0', '前沿科技产业的一个特点，就是总会给大家制造出各种各样的风口。这些风口往往有着特别酷炫的名字，有强大的学术论证和巨头背书，但真正落实到应用性和商业行为当中，结果怎么样又不好说了。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">&nbsp;<a href=\"http://s5.51cto.com/wyfs02/M01/9C/30/wKioL1ltdTCAwG1xAAA-o4vfudg313.jpg-wh_651x-s_1882044490.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s5.51cto.com/wyfs02/M01/9C/30/wKioL1ltdTCAwG1xAAA-o4vfudg313.jpg-wh_651x-s_1882044490.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">前沿科技产业的一个特点，就是总会给大家制造出各种各样的风口。这些风口往往有着特别酷炫的名字，有强大的学术论证和巨头背书，但真正落实到应用性和商业行为当中，结果怎么样又不好说了。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">比如说，作为脑极体关注的核心领域之一，大数据与云计算产业当中，能明显感觉到最近一个词热度蹿升的速度特别快：边缘计算。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">事实上，边缘计算并非新生事物，其运算原理在很多年前就已经被提出了。但随着近期物联网产业发展、5G时代到来，与这些领域息息相关的边缘计算又被重新推向了风口浪尖。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">去年11月，华为、沈阳自动化所、Intel、ARM、信通院、软通动力联合发起成立了边缘计算产业联盟(Edge Computing Consortium)，更是让整个2017年中国的ICT与物联网市场弥漫着边缘计算的芬芳。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s1.51cto.com/wyfs02/M01/9C/30/wKioL1ltdVbBHE7MAALuFS6L5ag685.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s1.51cto.com/wyfs02/M01/9C/30/wKioL1ltdVbBHE7MAALuFS6L5ag685.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">半年时间里，国内大批以边缘计算为卖点的智能家居和物联网企业狂飙突进，希望借助边缘计算的火爆成为“风口上的猪”。但边缘计算真有那么神吗?又是什么在吸引着大批跟风者进场?下面让我们辩证地走进这个“重要又不重要”的领域，讨论一下“后云计算”时代的物联网可能。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>边缘计算、雾计算都是些什么?</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">要讲边缘计算，没法绕过去的一个概念叫做“雾计算”。一听名字就知道这是从云计算那变化出来的。雾计算的原理与云计算一样，都是把数据上传到远程中心进行分析、存储和处理。但相比于云计算要把所有数据集中运输到同一个中心，雾计算的模式是设置众多分散的中心节点，即所谓“雾节点”来处理。这样能够让运算处理速度更快，更高效得出运算结果。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">假如说云计算是把所有东西都送往天上的云彩中，雾计算就是把数据送到身边的雾气里，这种逻辑被称为“分散式云计算”。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">但把云计算分散化的设想，不止产生了雾计算一个结果。有一种更激进的想法是，既然要分散，那为什么不干脆在最近距离完成计算?跑到云端或者跑到分散中心不都是跑吗?于是就有了所谓的边缘计算。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">边缘计算，是指利用靠近数据源的边缘地带来完成的运算程序。运算可以在大型运算设备内完成，也可以在中、小型运算设备、本地端网络内完成。而用于边缘运算的设备既可以是智能手机这样的移动设备，也可以是PC、智能家居等家用终端，甚至可以是ATM机、摄像头等市政终端。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">总之一句话，你附近哪能算就在哪算，越近越好。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s1.51cto.com/wyfs02/M01/9C/30/wKiom1ltdWTRAOJCAAER1AqMKuk847.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s1.51cto.com/wyfs02/M01/9C/30/wKiom1ltdWTRAOJCAAER1AqMKuk847.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">(边缘计算示意图)</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">举个不恰当但十分容易理解的例子：假如你手机里有个文件太大装不下了，云计算的解决方法是把它传到千里之外的服务器中心帮你存着，雾计算是传到小区的服务器帮你存着，边缘计算是看看你的电脑、冰箱、洗衣机谁有地方就帮你存哪……</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">显然，边缘计算运算设备和终端设备之间距离最近，传输效果最直接，还可以利用近距离网络和局域网络，当然运算效率也会更高一点。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>边缘计算更适合物联网?</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">边缘计算之所以短时间内名声鹊起，是与算法、数据和传输网络几个领域的发展密不可分的。但边缘计算带来的价值想象，最核心的一点还是它与一个词发生了难以描述的关系：物联网。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">我们知道，物联网能够达成的基础，是让设备连接网络，达成交互、数据收集和数据处理的能力。而现阶段物联网的主要运算能力都是由云计算提供的，边缘计算带来的几个好处，仿佛让物联网感觉到应该抛弃云计算去投怀送抱。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">首先，是边缘计算带来了更快的传输和响应速度。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">对于直接运用于民生、市政甚至工农业的物联网体系来说，效率和速度意味着一切。尤其是精密的生产型物联网，决不能容忍民用终端的延迟率。而云计算传输到云端，再把结果返回到终端的思路，显然不如边缘计算的就近原则来的快。要知道，再短的时间乘以整个终端的数量，都是令人震惊的产业效率。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">其次是部分摆脱了网络环境制约，也更加安全。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">举个例子，假如你的洗衣机、冰箱都是智能化控制的，而且依托于云计算。但你家没有停电，却断网了，那怎么办?无法进行云端传输，物联网设备就会歇业，岂不是是十分尴尬?边缘计算解决了这部分网络环境的限制。并且避免数据上传云端带来的泄露风险，所以更适合物联网体系。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s2.51cto.com/wyfs02/M02/9C/30/wKioL1ltddKTnJ19AAGxEozN1eE556.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s2.51cto.com/wyfs02/M02/9C/30/wKioL1ltddKTnJ19AAGxEozN1eE556.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">再次，边缘计算利用了传统云计算的遗漏区域，产能比更划算。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在整体上云的思路下，很大部分我们身边的运算能力将遭到放弃。这显然也是巨大的浪费，边缘计算将这部分能力调集了起来，形成了中心+分散的运算模式，产能比会更高，资源的利用率有大幅提升。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">这些优势，都让很多人看到了边缘计算的潜在价值。甚至喊出了“边缘计算干掉云计算”的口号，而在一些更有诱惑力的东西吸引下，一些物联网企业开始按捺不住，纷纷走向了豪赌边缘计算的道路。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>物联网企业大力拥抱边缘计算，可能有点小心思</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">先不判断这股行业风气的对错，我们先来看看相比技术逻辑，边缘计算带来的商业吸引力都来自哪里?</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">一、云计算被巨头切割之后，打开的机会缺口：经过几年的超高速发展，云计算已经在世界范围内成为了核心资源和社会生产力。可谓“举国上下尽知云”，但云计算的中心化特征，让这个领域注定是巨头们玩的游戏，小企业很难分得一杯羹。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">今天边缘计算带来了一种仿佛可以推翻云计算的可能，当然会吸引无数目光。毕竟云计算带来的巨型回报就在眼前。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s4.51cto.com/wyfs02/M02/9C/30/wKiom1ltddyzlRtlAADJ_V7klx4284.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s4.51cto.com/wyfs02/M02/9C/30/wKiom1ltddyzlRtlAADJ_V7klx4284.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">二、大公司相继背书。今年4月，全球最大开源社群Linux决定成立新项目来推动边缘计算。目前已经有的Dell、AMD等超过50家厂商加入。这只是众多边缘计算联盟中的一个。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">随着亚马逊、微软、英特尔等大企业相继入局，边缘计算的前景也在某种程度上成为了行业共识。希望跟风分蛋糕的小企业自然也敢于下筹码。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">三、更适合工业生产的物联网技术解决方案。相比较泛在化的云计算+物联网解决方案，边缘计算的特点之一是能大幅解决延迟率问题，对于工业生产体系至关重要。这也是边缘计算经常与“工业4.0”放在一起的原因。工业，意味着客户，意味着钱，当然是比其他飘在天上的物联网技术更受人待见。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">四、人工智能和机器人的应用落地：边缘计算的另一大优势，是为人工智能硬件、智能机器人提供高速交互所需的运算服务。这类智能产品的运算需求特别大，都挤压到云端对运算能力、传输能力是巨大考验，边缘计算能解决一部分问题。这就把边缘计算的前景和人工智能技术的前景捆绑到了一起，想象空间骤然加大。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s4.51cto.com/wyfs02/M00/9C/31/wKiom1ltdm6QHFupAAICc5pRTCo532.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s4.51cto.com/wyfs02/M00/9C/31/wKiom1ltdm6QHFupAAICc5pRTCo532.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">五、关乎5G这场大动作：更直接的一个风口，来自于我们翘首以盼的5G。在5G环境里，移动边缘计算将运算下沉到了用户附件，提供了高宽带、低延时的网络传输效果。这为5G传输提供了至关重要的保障，并且也提供了更好的用户体验。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">比如在线看视频，云计算传输在足够快的速度下也能保障流畅度，但却很容易造成画质损害。利用边缘计算保障视频传输理论上可以显著提高画质。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">这些非常明显的商业机遇，加速了边缘计算引发的风潮。但最后不得不泼一盆冷水：至少从目前来看，边缘计算取代云计算是基本不可能的事。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>边缘计算难对云计算构成挑战，也难独自存在</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">或许在未来某一天，我们可以享受到万物都是终端，同时万物都是计算中心的生活。但目前看起来完成大量的计算任务主要还是依靠云。边缘计算的设想很精密，但落地的应用却很少，尤其高度适配各个领域的技术解决方案似乎还没有出现。综合来看，边缘计算走向实际应用还有距离，更有两个难以跨越的难关：</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">一方面，边缘计算难以兼容异构。也就是说如何让冰箱算洗衣机的数据、空调算手机的数据还没人回答。这就导致了真正能提供边缘计算能力的设备和网络非常稀少。虽然边缘计算能借力打力，但压根没有可借的力就呜呼奈何了。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">另一方面，边缘计算的智能化进度也让人疑惑。我们知道，大数据处理和呈现，是高度依赖智能化的运算方式。将数据中心化集成，意味着可以通过不同算法整合处理，得到各种需要的结果。但边缘计算的运算场景却无法兼容过多智能化处理方式，这就让运算体系间的适用性不高。说白了，边缘计算快是快了，但代价是必然比云计算笨上很多。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">总而言之，边缘计算在很长一段时间里，是无法实际取代云计算的。更大概率是作为辅助手段与云计算相结合，作为补充手段和特定场景技术。</p><p><br/></p>', '51CTO.com', '佚名', '6', '', '更适合物联网的边缘计算，可能只是投机者的一块鸡肋', '1', '1', 'attachment/2017/07/25/39171500963068.jpg', '14', '0', '2017-07-21 17:00:24', '2017-07-25 14:11:14', '边缘计算');
INSERT INTO `article` VALUES ('7', '成功备战微服务的5个准备步骤', '8', '时至今日，微服务相关的话题不胜枚举，上百次的会议，在线讨论以及相关文章。你可以假设大家已经认识到其优点以及与之俱来的风险。然而，有很多组织没有事先准备就迈入这个潮流了。自然，这也就导致了在架构实现过程中的失败。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">时至今日，微服务相关的话题不胜枚举，上百次的会议，在线讨论以及相关文章。你可以假设大家已经认识到其优点以及与之俱来的风险。然而，有很多组织没有事先准备就迈入这个潮流了。自然，这也就导致了在架构实现过程中的失败。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">有一位智者曾经说过，“对于商业中所应用的任何技术而言，有2条规则，其一，将自动化应用于高效的运维上才能增加效率;其二，将自动化应用于低效的运维反而会降低效率。”我认为这种哲学对微服务而言亦行之有效。如果你的组织没有为此准备就贸然迁移，很可能会招致失败。以上就是本文的出发点，我将为大家带来在实现微服务架构前需要准备的5个关键步骤。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>1. 从绘图入手</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在开发特定的微服务时，许多开发者都会犯同一个错误：直接写代码。或许，这可能就是你能够犯的最严重的错误了。诚然，对于一个服务而言，你可能会获得成功，但是随着服务数量的上升，所有事情都会变得一团乱。和其它产品一样，它也需要被创造，需要以设计作为初始步骤。将团队汇聚到桌子周围，自由地在纸上(或者白板)绘制服务。首先，找出你所构建的应用的main函数。然后，自顶向下地将它分解成最小单元。最后，找出不同单元的互联通性。这些功能将会成为你的微服务。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s2.51cto.com/wyfs02/M00/9C/4B/wKioL1luwt6A3gkvAAHMsb83WyQ827.jpg-wh_651x-s_1254087759.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s2.51cto.com/wyfs02/M00/9C/4B/wKioL1luwt6A3gkvAAHMsb83WyQ827.jpg-wh_651x-s_1254087759.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">比如，在一款图书预览应用中，其主要的功能就是比较不同的图书。然而，也有许多其他功能需要开发，例如创建用户履历，评分，评论，图书数据库等。识别每个功能，是将它们转为微服务的关键。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">整个过程将会持续超过一天时间，并且需要多次迭代直至完美。当然，你将需要从许多不同部门的人员手中获得输入，以确保能够知晓其视角和观点，并确保你不会遗漏任何系统功能。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>2. 微服务并不是组织结构</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">根据你所在公司的组织结构定义微服务，这似乎是很自然的。如果你正在构建单体(monolithic)应用，这或许真是一个恰当的解决方案。但是，在实现微服务架构时，它可能就是一个错误的决定了。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">也许，这对于销售部门或客户服务是可行的，但是许多组织只有一个部门处理所有的数据库。因此，为所有数据库创建一个微服务将会导致单点故障。而没有单点故障，则是微服务的关键特性之一。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">你希望通过服务实现的一些功能可能会涉及几个组织部门，或者，你可能将会需要为一个部门构建许多微服务。总结一下，你需要将架构聚焦于你想要提供的服务，而非你们公司的组织结构。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>3. 创建合适的组织结构</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">转变到一个全然不同的组织架构，能够满足公司在管控活动上变化的需要。之前2个步骤关注于应用的设计，以便能够为最终用户提供正确的功能。现在，是时候确保对于新的架构而言，你拥有恰当的运维支持了。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">你将不得不放弃原有的部门结构，并使团队关注于某个微服务。这意味着这些团队将由拥有不同技能的成员组成，如系统分析师、UX/UI设计师、后端工程师、前端工程师等。如此一来，团队就能够彻底负责它们的项目(微服务)——从开发部署到运维、监控和管理。反过来，由于团队觉得自己拥有这个产品，因此将提升其创建产品的积极性。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s2.51cto.com/wyfs02/M01/9C/4C/wKiom1luwuqRCvpXAADYWvU5DQs217.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s2.51cto.com/wyfs02/M01/9C/4C/wKiom1luwuqRCvpXAADYWvU5DQs217.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">团队的规模视公司/项目的总体人数而定;当然，根据专家的建议，比较理想的规模是每个团队8-10人。和单体架构不同，微服务架构使得你能够根据业务的增长来扩展团队规模。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">当然，所有团队将会(需要)积极配合，以最终促成整个项目。这便是这种结构的主要好处，使我们能够尽可能快地向市场交付产品。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>4. 性能和可靠性同样重要</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">切换到微服务架构的整体想法，便是使得创建的最终产品相比于单体应用而言拥有更好的性能，更佳的稳定性(例如，更低的下线风险)，同时可以更快地交付市场。将改进性能作为设计的一部分是很重要的，这将确保潜在问题能够尽早被考虑。通常，(性能)问题都源自微服务设计主要考虑功能。然而，如果在更大的服务下，服务崩溃或者变得很慢，那么它就没什么用了。每个服务应该拥有2-3个替代机制，当下层资源失败时，它便可以继续运作。当其中一个机制无法在可接受时间窗口内响应时，服务也需要做到快速切换。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在前期考虑让服务支持更大的负载变动，你的应用将能够应对实际挑战，你将领先市场，当然这也是你在第一时间考虑切换到微服务的主要原因!</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>5. 变更先行</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">忽略应用的变化是不切实际的。开发者一直在增加和移除功能，变更代码，替换应用的核心元素等，这在微服务应用中更甚。更确切地说，微服务就是持续演进的。当你每天需要处理多次代码升级时，最好接受一个观点：变更是持续的，而非对于稳定状态的一次偶然性中断。一旦你认知到这一点，你将意识到一开始便整合变更的灵活性需求的重要性。确保应用一直正常工作的一种方式便是准备服务API，这样微服务间便可继续通信，即使它们已被改变。另外，你还需要引入版本控制，以允许服务同时开放新旧接口。另一方面，数据存储的演进是一个更大的挑战。改进数据库模式(schema)，使其支持新的功能，传统观点中，这是应用升级时最难处理的部分，微服务并不会使其变得更加简单。然而，单就增加新的域且不破坏现有结构这一点而言，NoSQL数据库会更加灵活。如果你希望你的数据存储需求持续演进(谁不希望呢?)，那么你应该将可演化的数据存储作为微服务设计的努力方向之一。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在迁移到微服务架构时，做适当的准备是整个项目成功的关键因素，这一点我们可以认同。只有经过仔细的准备，创新设计思维，并且具备合适的运维和管理结构，你才能收获微服务带来的所有好处!</p><p><br/></p>', '51CTO.com', '佚名', '7', '', '微服务，成功备战，构建单体(monolithic)应用', '1', '0', 'attachment/2017/07/25/18061500963101.jpg', '1', '0', '2017-07-21 17:09:41', '2017-07-25 14:11:45', '微服务');
INSERT INTO `article` VALUES ('8', 'Docker Compose：链接外部容器的几种方式', '5', '容器之间的链接是一种很常见的操作：它提供了访问其中的某个容器的网络服务而不需要将所需的端口暴露给Docker Host主机的功能。Docker Compose中对该特性的支持同样是很方便的。然而，如果需要链接的容器没有定义在同一个 docker-compose.yml 中的时候，这个时候就稍微麻烦复杂了点。', '<p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><a href=\"http://s2.51cto.com/wyfs02/M02/9C/20/wKiom1lsdkSQ5eZ7AAEmkilnbAc868.jpg-wh_651x-s_3859063289.jpg\" target=\"_blank\" style=\"color: rgb(255, 66, 0); transition: all 0.5s ease; text-decoration-line: none;\"><img src=\"http://s2.51cto.com/wyfs02/M02/9C/20/wKiom1lsdkSQ5eZ7AAEmkilnbAc868.jpg-wh_651x-s_3859063289.jpg\" border=\"0\" alt=\"\" title=\"\" style=\"border: none;\"/></a></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在Docker中，容器之间的链接是一种很常见的操作：它提供了访问其中的某个容器的网络服务而不需要将所需的端口暴露给Docker Host主机的功能。Docker Compose中对该特性的支持同样是很方便的。然而，如果需要链接的容器没有定义在同一个 docker-compose.yml 中的时候，这个时候就稍微麻烦复杂了点。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在不使用Docker Compose的时候，将两个容器链接起来使用 —link 参数，相对来说比较简单，以 nginx 镜像为例子：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">docker&nbsp;run&nbsp;--rm&nbsp;--name&nbsp;test1&nbsp;-d&nbsp;nginx&nbsp;&nbsp;#开启一个实例test1&nbsp;docker&nbsp;run&nbsp;--rm&nbsp;--name&nbsp;test2&nbsp;--link&nbsp;test1&nbsp;-d&nbsp;nginx&nbsp;#开启一个实例test2并与test1建立链接</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">这样， test2 与 test1 便建立了链接，就可以在 test2 中使用访问 test1 中的服务了。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">如果使用Docker Compose，那么这个事情就更简单了，还是以上面的 nginx 镜像为例子，编辑 docker-compose.yml 文件为：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">version:&nbsp;&quot;3&quot;&nbsp;services:&nbsp;&nbsp;test2:&nbsp;&nbsp;image:&nbsp;nginx&nbsp;&nbsp;depends_on:&nbsp;&nbsp;-&nbsp;test1&nbsp;&nbsp;links:&nbsp;&nbsp;-&nbsp;test1&nbsp;&nbsp;test1:&nbsp;&nbsp;image:&nbsp;nginx</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">最终效果与使用普通的Docker命令 docker run xxxx 建立的链接并无区别。这只是一种最为理想的情况。</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>如果容器没有定义在同一个 docker-compose.yml 文件中，应该如何链接它们呢?</p></li><li><p>又如果定义在 docker-compose.yml 文件中的容器需要与 docker run xxx 启动的容器链接，需要如何处理?</p></li></ol><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">针对这两种典型的情况，下面给出我个人测试可行的办法：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>方式一：让需要链接的容器同属一个外部网络</p></li></ol><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">我们还是使用nginx镜像来模拟这样的一个情景：假设我们需要将两个使用Docker Compose管理的nignx容器( test1 和 test2 )链接起来，使得 test2 能够访问 test1 中提供的服务，这里我们以能ping通为准。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">首先，我们定义容器 test1 的 docker-compose.yml 文件内容为：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">version:&nbsp;&quot;3&quot;&nbsp;services:&nbsp;&nbsp;test2:&nbsp;&nbsp;image:&nbsp;nginx&nbsp;&nbsp;container_name:&nbsp;test1&nbsp;&nbsp;networks:&nbsp;&nbsp;-&nbsp;default&nbsp;&nbsp;-&nbsp;app_net&nbsp;networks:&nbsp;&nbsp;app_net:&nbsp;&nbsp;external:&nbsp;true</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">容器 test2 内容与 test1 基本一样，只是多了一个 external_links ,需要特别说明的是： 最近发布的Docker版本已经不需要使用external_links来链接容器，容器的DNS服务可以正确的作出判断 ，因此如果你你需要兼容较老版本的Docker的话，那么容器 test2 的 docker-compose.yml文件内容为：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">version:&nbsp;&quot;3&quot;&nbsp;services:&nbsp;&nbsp;test2:&nbsp;&nbsp;image:&nbsp;nginx&nbsp;&nbsp;networks:&nbsp;&nbsp;-&nbsp;default&nbsp;&nbsp;-&nbsp;app_net&nbsp;&nbsp;external_links:&nbsp;&nbsp;-&nbsp;test1&nbsp;&nbsp;container_name:&nbsp;test2&nbsp;networks:&nbsp;&nbsp;app_net:&nbsp;&nbsp;external:&nbsp;true</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">否则的话， test2 的 docker-compose.yml 和 test1 的定义完全一致，不需要额外多指定一个 external_links 。相关的问题请参见stackoverflow上的相关问题： docker-compose + external container</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">正如你看到的那样，这里两个容器的定义里都使用了同一个外部网络 app_net ,因此，我们需要在启动这两个容器之前通过以下命令再创建外部网络：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">docker&nbsp;network&nbsp;create&nbsp;app_net</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">之后，通过 docker-compose up -d 命令启动这两个容器，然后执行 docker exec -it test2 ping test1 ,你将会看到如下的输出：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">docker&nbsp;exec&nbsp;-it&nbsp;test2&nbsp;ping&nbsp;test1&nbsp;PING&nbsp;test1&nbsp;(172.18.0.2):&nbsp;56&nbsp;data&nbsp;bytes&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.18.0.2:&nbsp;icmp_seq=0&nbsp;ttl=64&nbsp;time=0.091&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.18.0.2:&nbsp;icmp_seq=1&nbsp;ttl=64&nbsp;time=0.146&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.18.0.2:&nbsp;icmp_seq=2&nbsp;ttl=64&nbsp;time=0.150&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.18.0.2:&nbsp;icmp_seq=3&nbsp;ttl=64&nbsp;time=0.145&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.18.0.2:&nbsp;icmp_seq=4&nbsp;ttl=64&nbsp;time=0.126&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.18.0.2:&nbsp;icmp_seq=5&nbsp;ttl=64&nbsp;time=0.147&nbsp;ms</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">证明这两个容器是成功链接了，反过来在 test1 中ping test2 也是能够正常ping通的。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">如果我们通过 docker run --rm --name test3 -d nginx 这种方式来先启动了一个容器( test3 )并且没有指定它所属的外部网络，而需要将其与 test1 或者 test2 链接的话，这个时候手动链接外部网络即可：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">docker&nbsp;network&nbsp;connect&nbsp;app_net&nbsp;test3</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">这样，三个容器都可以相互访问了。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\"><strong>方式二：更改需要链接的容器的网络模式</strong></p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">通过更改你想要相互链接的容器的网络模式为 bridge ,并指定需要链接的外部容器( external_links )即可。与同属外部网络的容器可以相互访问的链接方式一不同，这种方式的访问是单向的。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">还是以nginx容器镜像为例子，如果容器实例 nginx1 需要访问容器实例 nginx2 ，那么 nginx2 的 doker-compose.yml 定义为：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">version:&nbsp;&quot;3&quot;&nbsp;services:&nbsp;&nbsp;nginx2:&nbsp;&nbsp;image:&nbsp;nginx&nbsp;&nbsp;container_name:&nbsp;nginx2&nbsp;&nbsp;network_mode:&nbsp;bridge</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">与其对应的， nginx1 的 docker-compose.yml 定义为：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">version:&nbsp;&quot;3&quot;&nbsp;services:&nbsp;&nbsp;nginx1:&nbsp;&nbsp;image:&nbsp;nginx&nbsp;&nbsp;external_links:&nbsp;&nbsp;-&nbsp;nginx2&nbsp;&nbsp;container_name:&nbsp;nginx1&nbsp;&nbsp;network_mode:&nbsp;bridge</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">需要特别说明的是，这里的 external_links 是不能省略的，而且 nginx1 的启动必须要在 nginx2 之后，否则可能会报找不到容器 nginx2 的错误。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">接着我们使用ping来测试下连通性：</p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 1em; font-family: \" courier=\"\" font-size:=\"\" width:=\"\" overflow:=\"\" color:=\"\" line-height:=\"\" widows:=\"\" background-color:=\"\">$&nbsp;docker&nbsp;exec&nbsp;-it&nbsp;nginx1&nbsp;ping&nbsp;nginx2&nbsp;&nbsp;#&nbsp;nginx1&nbsp;to&nbsp;nginx2&nbsp;PING&nbsp;nginx2&nbsp;(172.17.0.4):&nbsp;56&nbsp;data&nbsp;bytes&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.17.0.4:&nbsp;icmp_seq=0&nbsp;ttl=64&nbsp;time=0.141&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.17.0.4:&nbsp;icmp_seq=1&nbsp;ttl=64&nbsp;time=0.139&nbsp;ms&nbsp;64&nbsp;bytes&nbsp;from&nbsp;172.17.0.4:&nbsp;icmp_seq=2&nbsp;ttl=64&nbsp;time=0.145&nbsp;ms&nbsp;&nbsp;$&nbsp;docker&nbsp;exec&nbsp;-it&nbsp;nginx2&nbsp;ping&nbsp;nginx1&nbsp;#nginx2&nbsp;to&nbsp;nginx1&nbsp;ping:&nbsp;unknown&nbsp;host</pre><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">以上也能充分证明这种方式是属于单向联通的。</p><p style=\"padding: 0px 0px 15px; margin-top: 0px; margin-bottom: 0px; color: rgb(51, 51, 51); font-family: \" microsoft=\"\" font-size:=\"\" text-align:=\"\" line-height:=\"\" word-wrap:=\"\" white-space:=\"\" widows:=\"\" background-color:=\"\">在实际应用中根据自己的需要灵活的选择这两种链接方式，如果想偷懒的话，大可选择第二种。不过我更推荐第一种，不难看出无论是联通性还是灵活性，较为更改网络模式的第二种都更为友好。</p><p><br/></p>', '51CTO.com', '佚名', '8', '', 'Docker Compose，链接外部容器，nginx 镜像，外部网络', '1', '0', 'attachment/2017/07/25/45481500942461.jpg', '1', '0', '2017-07-21 17:14:13', '2017-07-25 14:11:54', '链接外部容器');

-- ----------------------------
-- Table structure for `attachment`
-- ----------------------------
CREATE TABLE `attachment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL COMMENT '会员编号',
  `name` varchar(80) NOT NULL,
  `filename` varchar(300) NOT NULL COMMENT '文件名',
  `path` varchar(300) NOT NULL COMMENT '文件路径',
  `extension` varchar(10) NOT NULL DEFAULT '' COMMENT '文件类型',
  `createtime` int(10) NOT NULL COMMENT '上传时间',
  `size` mediumint(9) NOT NULL COMMENT '文件大小',
  `data` varchar(100) NOT NULL DEFAULT '' COMMENT '辅助信息',
  `status` tinyint(1) unsigned NOT NULL COMMENT '状态',
  `content` text NOT NULL COMMENT '扩展数据内容',
  PRIMARY KEY (`id`),
  KEY `data` (`data`),
  KEY `extension` (`extension`),
  KEY `status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=63 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='附件';

